echo ""|awk '{W=100;H=100;WW=W+10;srand();for(i=0;i<H;i++){for(j=0;j<W;j++){if(rand()>0.6)map[i*WW+j]=" ";else map[i*WW+j]="X";}}while(1==1){cnt=0;for(i=0;i<H;i++){for(j=0;j<W;j++){   cnt=0;for(ii=-2;ii<=2;ii++){for(jj=-2;jj<=2;jj++){if(ii==0&&jj==0)continue;if((i>=-1&&i<=1)&&(j>=-1&&j<=1)&&map[(i+ii)*WW+(j+jj)]=="X")cnt++;else if(map[(i+ii)*WW+(j+jj)]=="X")cnt+=0.7;}}if(map[i*WW+j]=="X"){if(cnt<=6)map2[i*WW+j]=" ";else if(cnt>=10)map2[i*WW+j]=" ";else map2[i*WW+j]="X"}else{if(cnt>=5)map2[i*WW+j]="X";else map2[i*WW+j]=" ";}}}for(i=0;i<H;i++){for(j=0;j<W;j++){map[i*WW+j]=map2[i*WW+j];}}system("clear");for(j=0;j<60;j++)printf("="); printf("\n");for(i=30;i<60;i++){for(j=10;j<90;j++){printf("%c",map[i*WW+j]);}printf("\n");}for(j=0;j<60;j++)printf("="); system("sleep 0.1");printf("\n");}}'
